import { Pagination, Layout, Input, Col, Row, Button, message, Card, Modal, Image } from 'antd';
import HeaderStaff from '../../components/staff/Header'
const { Meta } = Card;
import { useEffect, useState } from "react";
import { getAllCrime, getCrime } from '../../utils/api-services/crime-service'
import styles from '../../styles/Custom.module.css'
import { InfoCircleOutlined } from '@ant-design/icons';

export default function CrimeList() {
  const [reset, setReset] = useState(new Date());
  const [searchValue, setSearchValue] = useState('');
  const [crimes, setCrimes] = useState([]);
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(12);
  const [total, setTotal] = useState(0);

  useEffect(async () => {
    try {
      const data = await getAllCrime({ page, size, searchValue });
      if (data.success) {
        setCrimes(data.data)
        setTotal(data.total)
      }
    } catch (error) {
      message.error("Fetch Data Crime error " + error.toString());
    }
  }, [reset, page, size, searchValue])


  const onSearch = e => setSearchValue(e.target.value)

  const inforCrime = async (id) => {
    try {
      const data = await getCrime(id);
      if (data.success) {
        const crime = data.data;
        Modal.info({
          title: crime.name,
          content: (
            <div style={{ width: 500 }}>
              <p>Name: {crime.name}</p>
              <p>ID Number: {crime.id_number}</p>
              <p>Other Information:</p>
              <p>Image:
                <Image src={crime.image} width={200} height={200} style={{ margin: '12px 32px' }} />
              </p>
              <p>Face Image:
                <Image src={crime.face_image} width={200} height={200} style={{ margin: 12 }} />
              </p>
            </div>
          ),
          onOk() { },
          width: 540,
          style: { top: 20 }
        });
      }
    } catch (error) {
      message.error("Fetch Data Crime error " + error.toString());
    }
  }

  return <Layout className={"layout"}>
    <HeaderStaff selectedKey="3" />
    <div style={{ padding: '18px 90px' }}>
      <Input.Search
        placeholder="Search name of Crime"
        allowClear
        onChange={onSearch}
        style={{ width: '100%', margin: '12px 0px' }}
        size="large"
        value={searchValue}
      />
      <Row gutter={[16, 16]}>
        {
          crimes.map((item) => <Col sm={6} xxl={4} xs={8} key={item.id}>
            <Card
              style={{ width: '100%' }}
              className={styles.cardCustom}
              cover={
                <>
                  <img
                    alt="example"
                    src={item.image ? item.image : "data:image/png;base64,"}
                    style={{ width: '100%', position: "relative", top: 0, left: 0, height: 250 }}
                  />

                  <img
                    src="https://www.onlygfx.com/wp-content/uploads/2017/11/wanted-stamp-3.png"
                    style={{ opacity: 0.3, position: "absolute", left: '50%', top: '50%', transform: 'translate(-50%, -50%)', maxWidth: 250, maxHeight: 200 }}
                  />

                </>
              }
              actions={[
                <InfoCircleOutlined key="infor" onClick={() => inforCrime(item.id)} />,
              ]}
            >
              <Meta
                // avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                title={item.name}
                description={item.id_number}
              />
            </Card>
          </Col>)
        }
      </Row>
      <div style={{ width: "100%", textAlign: "right" }}>
        <Pagination
          total={total}
          showTotal={(total, range) => `${range[0]}-${range[1]} of ${total} items`}
          pageSize={size}
          current={page}
          onChange={(page, size) => {
            setPage(page);
            setSize(size);
          }}
          pageSizeOptions={[12, 24, 48, 96]}
          showSizeChanger
        />
      </div>
    </div>
  </Layout>
}